mapper.select:
SELECT price_id, type_id, specific_date, price, remark, create_time
FROM daily_prices
WHERE type_id = #{typeId}
AND specific_date BETWEEN #{startDate} AND #{endDate}
ORDER BY specific_date

mapper.update:
UPDATE daily_prices
SET price = #{price},
    remark = #{remark}
WHERE type_id = #{typeId} AND specific_date = #{specificDate}
