mapper.select:
SELECT site_id, type_id, site_no, status, create_time, update_time
FROM sites
WHERE site_id = #{siteId}

SELECT s.site_id, s.type_id, s.site_no, s.status, s.create_time, s.update_time
FROM sites s
WHERE s.type_id = #{typeId}
AND s.status = 1
AND s.site_id NOT IN (
    SELECT DISTINCT b.site_id
    FROM bookings b
    WHERE b.type_id = #{typeId}
    AND b.status IN (0, 1)
    AND NOT (b.check_out <= #{checkIn} OR b.check_in >= #{checkOut})
)
ORDER BY s.site_no
LIMIT #{limit}

mapper.update:
UPDATE sites
SET type_id = #{typeId},
    site_no = #{siteNo},
    status = #{status},
    update_time = #{updateTime}
WHERE site_id = #{siteId}
